 * These constants are used in the effect to calculate worldX and worldY. Including them ensures the effect
 * would re-run if their values were to change (though they are constants and won't change in this case).
 * This change eliminates the ESLint warning while maintaining the same functionality.
 */

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', fontFamily: 'Arial', background: '#222', color: '#fff' }}>
      <div style={{ margin: '10px', fontSize: '1.2em' }}>
        Balance: ${balance.toFixed(2)} | Multiplier: {multiplier.toFixed(2)}x | Phase: {phase} | {phase === 'waiting' ? `Countdown: ${countdown}s` : ''}
        {userName && phase === 'waiting' && (
          <>
            Bet: $
            <input
              type="number"
              value={bet}
              min="1"
              max={balance}
              style={{ width: '60px' }}
              onChange={(e) => setBet(Math.max(1, Math.min(balance, parseFloat(e.target.value))))}
            />
          </>
        )}
      </div>
      {!userName ? (
        <div>
          Enter your name:
          <input value={nameInput} onChange={(e) => setNameInput(e.target.value)} />
          <button onClick={setMyName}>Set Name</button>
        </div>
      ) : null}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-start' }}>
        <canvas ref={canvasRef} width={CANVAS_WIDTH} height={CANVAS_HEIGHT} style={{ border: '2px solid #fff', background: '#333' }} />
        <div style={{ marginLeft: '20px', width: '200px' }}>
          <h3>Leaderboard</h3>
          {leaderboard.map((entry, i) => (
            <div key={i}>{entry.name}: {entry.result} - ${entry.money}</div>
          ))}
        </div>
      </div>
      {phase === 'running' && hasBet && (
        <button onClick={cashOut} style={{
          position: 'fixed',
          left: '20px',
          top: '50%',
          transform: 'translateY(-50%)',
          padding: '10px 20px',
          fontSize: '1em',
          cursor: 'pointer',
          background: '#28a745',
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
        }}>
          Cash Out
        </button>
      )}
      {phase === 'waiting' && !hasBet && userName && (
        <button onClick={placeBet} style={{
          padding: '10px 20px',
          margin: '10px',
          fontSize: '1em',
          cursor: 'pointer',
          background: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
        }}>
          Place Bet & Join
        </button>
      )}
    </div>
  );
}

export default CrashGame;
