import React, { useState, useEffect, useRef, useCallback } from 'react';
import io from 'socket.io-client';

const SOCKET_URL = 'http://localhost:3001';

function CrashGame() {
  const [balance, setBalance] = useState(1000);
  const [bet, setBet] = useState(10);
  const [multiplier, setMultiplier] = useState(1);
  const [phase, setPhase] = useState('waiting');
  const [countdown, setCountdown] = useState(10);
  const [hasBet, setHasBet] = useState(false);
  const canvasRef = useRef(null);
  const timeRef = useRef(0);
  const particlesRef = useRef([]);
  const trailRef = useRef([]);
  const socket = useRef(null);
  const lastMultiplierRef = useRef(1);
  const lastUpdateTimeRef = useRef(Date.now());

  const updateParticles = useCallback((rocketX, rocketY) => {
    if (phase === 'running' && particlesRef.current.length < 100) {
      for (let i = 0; i < 2; i++) {
        particlesRef.current.push({
          x: rocketX + (Math.random() - 0.5) * 10,
          y: rocketY + 10,
          vx: (Math.random() - 0.5) * 2,
          vy: 1 + Math.random() * 2,
          life: 1.0,
        });
      }
    }
    particlesRef.current.forEach(p => {
      p.x += p.vx;
      p.y += p.vy;
      p.life -= 0.02;
      if (p.life < 0) console.warn('Negative life detected:', p.life);
    });
    particlesRef.current = particlesRef.current.filter(p => p.life > 0);
  }, [phase]);

  useEffect(() => {
    socket.current = io(SOCKET_URL);

    socket.current.on('gameUpdate', (data) => {
      setPhase(data.phase);
      setMultiplier(data.multiplier);
      setBalance(data.balance);
      setCountdown(data.countdown || 0);
    });

    socket.current.on('multiplierUpdate', (newMultiplier) => {
      setMultiplier(newMultiplier);
      lastMultiplierRef.current = newMultiplier;
      lastUpdateTimeRef.current = Date.now();
    });

    socket.current.on('waitingPhase', ({ countdown }) => {
      setPhase('waiting');
      setCountdown(countdown);
      setHasBet(false);  // Reset for new round
      timeRef.current = 0;
      trailRef.current = [];
      particlesRef.current = [];
    });

    socket.current.on('gameStarted', () => {
      setPhase('running');
    });

    socket.current.on('gameCrashed', ({ crashPoint }) => {
      setPhase('crashed');
      alert(`Crashed at ${crashPoint.toFixed(2)}x!`);
      // Handle loss if not cashed out
    });

    socket.current.on('balanceUpdate', (newBalance) => setBalance(newBalance));
    socket.current.on('cashOutSuccess', ({ multiplier, winnings }) => {
      alert(`Cashed out at ${multiplier.toFixed(2)}x! Won $${winnings.toFixed(2)}`);
    });

    return () => socket.current.disconnect();
  }, []);

  const placeBet = () => {
    if (phase !== 'waiting' || bet > balance || bet <= 0 || hasBet) return;
    socket.current.emit('placeBet', bet);
    setHasBet(true);
  };

  const cashOut = () => {
    if (phase !== 'running' || !hasBet) return;
    socket.current.emit('cashOut');
  };

  useEffect(() => {
  let frameId;
  const animate = () => {
    if (!canvasRef.current) return;
    const ctx = canvasRef.current.getContext('2d');

    // Interpolate multiplier for smoothness (assume ~0.01 increase per 100ms from server)
    const now = Date.now();
    const timeSinceLastUpdate = (now - lastUpdateTimeRef.current) / 1000;  // in seconds
    const estimatedMultiplier = lastMultiplierRef.current + (timeSinceLastUpdate * 0.1);  // Adjust 0.1 based on your server's increment rate (0.01 every 100ms = 0.1 per second)

    // Use estimatedMultiplier for position calc to make it feel continuous
    let time;
    if (estimatedMultiplier > 1) {
      time = Math.log(estimatedMultiplier - 1) / 0.5;
    } else {
      time = 0;
    }
    const rocketY = 400 - (Math.pow(Math.E, time * 0.5) * 100);
    const rocketX = (Math.pow(Math.E, time * 0.5) - 1) * 100;

    // Clear and draw axes (unchanged)
    ctx.clearRect(0, 0, 1200, 800);
    ctx.beginPath();
    ctx.moveTo(0, 800);
    ctx.lineTo(1200, 800);
    ctx.moveTo(0, 400);
    ctx.lineTo(0, 0);
    ctx.strokeStyle = '#fff';
    ctx.lineWidth = 1;
    ctx.stroke();

    // Particles draw (unchanged)
    particlesRef.current.forEach(p => {
      ctx.beginPath();
      ctx.arc(p.x, p.y, Math.max(0, 3 * p.life), 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 128, 0, ${Math.max(0, p.life)})`;
      ctx.fill();
    });

    // Trail: Only push every ~100ms to avoid bloat, but draw always
    if (phase === 'running' && (now - (trailRef.current[trailRef.current.length - 1]?.timestamp || 0) > 100)) {
      trailRef.current.push({ x: rocketX, y: rocketY, timestamp: now });
      if (trailRef.current.length > 200) trailRef.current.shift();  // Cap to prevent memory growth
    }
    ctx.beginPath();
    ctx.strokeStyle = '#fff';
    ctx.lineWidth = 2;
    trailRef.current.forEach((point, i) => {
      if (i === 0) ctx.moveTo(point.x, point.y);
      else ctx.lineTo(point.x, point.y);
    });
    ctx.stroke();

    // Rocket draw (unchanged)
    if (phase !== 'crashed') {
      ctx.beginPath();
      ctx.arc(rocketX, rocketY, 5, 0, Math.PI * 2);
      ctx.fillStyle = '#fff';
      ctx.fill();
    }

    // Particle updates: Run every frame for smoothness, but create fewer
    if (phase === 'running') {
      updateParticles(rocketX, rocketY);  // Your existing function
    }

    // Crashed explosion (unchanged, but runs every frame until particles fade)
    if (phase === 'crashed') {
      if (particlesRef.current.length < 200) {  // Burst once
        for (let i = 0; i < 50; i++) {
          particlesRef.current.push({
            x: rocketX + (Math.random() - 0.5) * 20,
            y: rocketY + (Math.random() - 0.5) * 20,
            vx: (Math.random() - 0.5) * 5,
            vy: (Math.random() - 0.5) * 5,
            life: 1.0,
          });
        }
      }
      particlesRef.current.forEach(p => {
        p.x += p.vx;
        p.y += p.vy;
        p.life -= 0.02;
      });
      particlesRef.current = particlesRef.current.filter(p => p.life > 0);
    }

    frameId = requestAnimationFrame(animate);
  };

  if (phase === 'running' || phase === 'crashed') {  // Only animate during active phases
    animate();
  }

  return () => cancelAnimationFrame(frameId);
}, [phase, updateParticles]);  // Restart loop on phase change

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', fontFamily: 'Arial', background: '#222', color: '#fff' }}>
      <div style={{ margin: '10px', fontSize: '1.2em' }}>
        Balance: ${balance.toFixed(2)} | Multiplier: {multiplier.toFixed(2)}x | Phase: {phase} | {phase === 'waiting' ? `Countdown: ${countdown}s` : ''}
        {phase === 'waiting' && (
          <>
            Bet: $
            <input
              type="number"
              value={bet}
              min="1"
              max={balance}
              style={{ width: '60px' }}
              onChange={(e) => setBet(Math.max(1, Math.min(balance, parseFloat(e.target.value))))}
            />
          </>
        )}
      </div>
      <canvas ref={canvasRef} width={600} height={400} style={{ border: '2px solid #fff', background: '#333' }} />
      {phase === 'running' && hasBet && (
        <button onClick={cashOut} style={{
          padding: '10px 20px',
          margin: '10px',
          fontSize: '1em',
          cursor: 'pointer',
          background: '#28a745',
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
        }}>
          Cash Out
        </button>
      )}
      {phase === 'waiting' && !hasBet && (
        <button onClick={placeBet} style={{
          padding: '10px 20px',
          margin: '10px',
          fontSize: '1em',
          cursor: 'pointer',
          background: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
        }}>
          Place Bet & Join
        </button>
      )}
    </div>
  );
}

export default CrashGame;
